'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Loader2, AlertCircle, FileCheck2, Upload } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

export default function DocumentCheckClient() {
  const { toast } = useToast();
  const [file, setFile] = useState<File | null>(null);
  const [report, setReport] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setFile(event.target.files[0]);
      setReport(null);
      setError(null);
    }
  };

  const handleCheckDocument = async () => {
    if (!file) {
      setError('Please select a file first.');
      return;
    }

    console.log('File selected:', file.name, file.type, file.size);

    setIsLoading(true);
    setError(null);
    setReport(null);

    try {
      // Read file immediately using Promise-based approach
      const documentDataUri = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result as string);
        reader.onerror = () => reject(reader.error);
        reader.readAsDataURL(file);
      });

      console.log('File loaded successfully, Data URI length:', documentDataUri.length);

      console.log('Calling API...');
      const response = await fetch('/api/document-check', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ documentDataUri }),
      });

      console.log('API response status:', response.status);

      if (!response.ok) {
        const errorData = await response.text();
        console.error('API error:', errorData);
        throw new Error('Failed to analyze document');
      }

      const result = await response.json();
      console.log('Analysis complete');
      setReport(result.report);
    } catch (e) {
      console.error('Error:', e);
      const errorMessage = e instanceof Error ? e.message : 'An unexpected error occurred during analysis.';
      setError(errorMessage);
      toast({
        variant: 'destructive',
        title: 'Error Checking Document',
        description: errorMessage,
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-8">
      <Card>
        <CardHeader>
          <CardTitle>Upload Document</CardTitle>
          <CardDescription>Select a document file (e.g., PDF, DOCX, PNG, JPG) to be analyzed.</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid w-full max-w-sm items-center gap-1.5">
            <Label htmlFor="document">Document</Label>
            <div className="flex gap-4">
              <Input id="document" type="file" onChange={handleFileChange} />
              <Button
                onClick={handleCheckDocument}
                disabled={!file || isLoading}
                type="button"
              >
                {isLoading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing...
                  </>
                ) : (
                  <>
                    <FileCheck2 className="mr-2 h-4 w-4" />
                    Check Document
                  </>
                )}
              </Button>
            </div>
          </div>
          {file && <p className="text-sm text-muted-foreground">Selected file: {file.name}</p>}
        </CardContent>
      </Card>

      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertTitle>Error</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {report && (
        <Card>
          <CardHeader>
            <CardTitle>Analysis Report</CardTitle>
            <CardDescription>The AI has reviewed your document. Here are the findings:</CardDescription>
          </CardHeader>
          <CardContent>
            <pre className="bg-muted p-4 rounded-lg whitespace-pre-wrap font-sans text-sm">{report}</pre>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

