  const [isInterviewStarted, setIsInterviewStarted] = useState(false);

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      visaType: 'Student Visa',
      destination: '',
      userBackground: '',
    },
  });

  async function handleStartInterview(values: z.infer<typeof formSchema>) {
    setIsLoading(true);
    setError(null);
    setCurrentQuestion(null);
    setInterviewHistory([]);
    setIsInterviewStarted(true);

    try {
      const data = await generateInterviewQuestion({
        visaType: values.visaType,
        destination: values.destination,
        userBackground: values.userBackground,
        previousQuestions: [],
      });
      setCurrentQuestion(data.question);
    } catch (e) {
      const errorMessage = e instanceof Error ? e.message : 'An unexpected error occurred.';
      setError(errorMessage);
      toast({
        variant: 'destructive',
        title: 'Error Starting Interview',
        description: errorMessage,
      });
      setIsInterviewStarted(false);
    } finally {
      setIsLoading(false);
    }
  }

  async function handleNextQuestion() {
    if (!currentQuestion || !currentAnswer.trim()) return;

    setIsLoading(true);
    setError(null);
    const newTurn: InterviewTurn = { question: currentQuestion, answer: currentAnswer };
    const updatedHistory = [...interviewHistory, newTurn];
    setInterviewHistory(updatedHistory);
    setCurrentQuestion(null);
    setCurrentAnswer('');

    try {
      const previousQuestions = updatedHistory.map(turn => turn.question);
      const formData = form.getValues();

      const data = await generateInterviewQuestion({
        visaType: formData.visaType,
        destination: formData.destination,
        userBackground: formData.userBackground,
        previousQuestions,
      });

      setCurrentQuestion(data.question);
    } catch (e) {
      const errorMessage = e instanceof Error ? e.message : 'An unexpected error occurred.';
      setError(errorMessage);
      toast({
        variant: 'destructive',
        title: 'Error Getting Next Question',
        description: errorMessage,
      });
    } finally {
      setIsLoading(false);
    }
  }

  const renderSetup = () => (
    <Card>
      <CardHeader>
        <CardTitle>Prepare for your Interview</CardTitle>
        <CardDescription>
          Fill out your details to begin a mock interview session with our AI.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleStartInterview)} className="space-y-8">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <FormField
                control={form.control}
                name="visaType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Visa Type</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select a visa type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Student Visa">Student Visa</SelectItem>

