            <SheetTitle>Japa Genie Assistant</SheetTitle>
          </SheetHeader>
          <div className="flex-1 overflow-y-auto">
            <ChatPanel />
          </div>
        </SheetContent>
      </Sheet>
    </>
  );
}

// ALSO export as default so any parent can import either way
export default FloatingChatButton;

